<?php $sidebar_position=th_theme_data('home_sidebar_position','right'); ?><?php get_header(); ?>        <section class="i-blog-section">        <div class="container">            <div class="row">              	<div class="i-blog-s-title">	            	<h1>                                               	                    <strong><?php printf( __( 'All posts by %s', 'larx' ), '<a href="' . esc_url( get_author_posts_url( get_the_author_meta( 'ID' ) ) ) . '" title="' . esc_attr( get_the_author() ) . '" rel="me">' . get_the_author() . '</a>' ); ?></strong>	                </h1>                </div>                          <?php                if($sidebar_position == "no"){                    $page_class="clearfix col-lg-12 col-md-12";                }else{                    $page_class="clearfix  col-lg-9";                }                ?>                <?php                if($sidebar_position == "left"){                    $page_class .=' pull-right';                }                ?>                <div class="<?php echo esc_attr($page_class); ?>">                    <?php echo get_template_part('loop')?>                </div><!-- single-post-wrapper -->                <?php if($sidebar_position != "no"){ ?>                    <div id="sidebar" class="col-lg-3">                        <?php get_sidebar(); ?>                    </div><!-- end sidebar -->                <?php } ?>            </div>        </div><!-- end container -->		<div class="text-center i-blog-pagination">            <?php echo paginate_links(); ?>        </div>    </section><!-- end section-single --><?php get_footer(); ?>