<?php $sidebar_position=th_theme_data('home_sidebar_position','right'); ?><?php get_header(); ?>        <section class="i-blog-section">        <div class="container">            <div class="row">                <div class="i-blog-s-title">                    <h1>                                               <strong><?php printf( __( 'Search Results for: %s', 'larx' ), get_search_query() ); ?></strong>                    </h1>                </div>                <?php                if($sidebar_position == "no"){                    $page_class="clearfix col-lg-12 col-md-12";                }else{                    $page_class="clearfix  col-lg-9";                }                ?>                <?php                if($sidebar_position == "left"){                    $page_class .=' pull-right';                }                ?>                <div class="<?php echo esc_attr($page_class); ?>">                    <?php                    if(have_posts()) {                        echo get_template_part('loop');                    }else{                        ?>                        <p class="alert alert-warning"><?php esc_html_e( 'Sorry, but nothing matched your search terms. Please try again with different keywords.', 'larx' ); ?></p>                    <?php                    }                    ?>                </div><!-- single-post-wrapper -->                <?php if($sidebar_position != "no"){ ?>                    <div id="sidebar" class="col-lg-3">                        <?php get_sidebar(); ?>                    </div><!-- end sidebar -->                <?php } ?>            </div>        </div><!-- end container -->		<div class="text-center i-blog-pagination">            <?php echo paginate_links(); ?>        </div>    </section><!-- end section-single --><?php get_footer(); ?>